import React, { useState } from 'react';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBold, faCaretSquareLeft, faCaretSquareRight, faEye, faSearch } from '@fortawesome/free-solid-svg-icons';
import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import Modal from 'react-bootstrap/Modal';
import { data } from './data.js';

function Visualiza() {
  const [search, setSearch] = useState('');
  const [selectedItem, setSelectedItem] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [esportes, setEsportes] = useState([
  "Todos",
  "Futebol",
  "Volei",
  "Xadrez",
  "Basquete",
  "Handbol",
  "Truco",
  "Tenis",
  "Natação",
  "Corrida"
]);
const [esporteAtual, setEsporteAtual] = useState(0);


  function handleOpenModal(item) {
    setSelectedItem(item);
    setShowModal(true);
  }
  function handleLast() {
    setEsporteAtual((prevEsporte) =>
      prevEsporte === 0 ? esportes.length - 1 : prevEsporte - 1
    );
  }
  
  function handleNext() {
    setEsporteAtual((prevEsporte) =>
      prevEsporte === esportes.length - 1 ? 0 : prevEsporte + 1
    );
  }
  
  return (
    <div className="App">
      <div style={{ backgroundColor: '#51AFF3' }}>

        {/* Cabeçalho e Titulo */}
        <header style={{ height: '85px', backgroundColor: '#2759A4' }}>
          <img src="LogoPrefeitura.png" alt="LogoPrefeitura" style={{ maxHeight: '65px', maxWidth: '220px', minHeight: '75px', minWidth: '150px' }} />
        </header>
        <h1 style={{ color: '#F5F5F5', padding: '20px' }}>ATLETAS REGISTRADOS</h1>


        {/* Imagem */}
        <div className="form-group">
          <img src="Esporte.png" alt="Esporte" style={{ borderRadius: 10, margin: '40px' }} />
        </div>


        {/* Menu de seleção de esporte */}
        <div className="form-group" style={{ display: 'flex', justifyContent: 'center' }}>
        
        {/* Botao Anterior */}
        <button
  id="btnLast"
  style={{
    height: "45px",
    backgroundColor: "#51AFF3",
    borderRadius: 7,
    borderColor: "#2759A4",
    marginRight: 55
  }}
  onClick={handleLast}
>
  <FontAwesomeIcon icon={faCaretSquareLeft} size="3x" color="#2759A4" />
</button>


{/* Nome do Esporte */}
          <h2 id="titEsporte" style={{ color: "#F5F5F5", margin: 5 }}>
  {esportes[esporteAtual]}
</h2>


{/* Botao Proximo */}
<button
  id="btnNext"
  style={{
    height: "45px",
    backgroundColor: "#51AFF3",
    borderRadius: 7,
    borderColor: "#2759A4",
    marginLeft: 55
  }}
  onClick={handleNext}
>
  <FontAwesomeIcon icon={faCaretSquareRight} size="3x" color="#2759A4" />
</button>
        </div>

        <div className="form-group" style={{ display: 'flex', justifyContent: 'center', marginTop: '40px' }}>
          
          
          {/* Campo de Pesquisa */}
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
            <Container>
              <Form>
                <InputGroup className="my-3">
                  <FormControl onChange={(e) => setSearch(e.target.value)} placeholder="Pesquisar" style={{ borderRadius: 7, height: '30px', width: '250px', marginBottom: 20 }} />
                  {/* <button style={{ height: '40px', width: '40px', backgroundColor: '#2759A4', borderRadius: 20, borderColor: '#2759A4',marginLeft: '20px' }}>
                    <FontAwesomeIcon icon={faSearch} size='xl' color='#51AFF3'/>
                  </button> */}
                </InputGroup>
              </Form>


              {/* Construção da tabela usando as classes criadas no css */}
              <Table className="table table-striped table-bordered table-hover custom-table">
                <tbody>
                  {data
                  
                  // Filtro definido pelo campo de pesquisa usando uma condicional ternaria
                    .filter((item) => {
                      return search.toLowerCase() === '' ? item : item.name.toLowerCase().includes(search);
                    })

                    // Chamada dos itens filtrados pelo seu index
                    .map((item, index) => (
                      <tr key={index}>
                        <td>

                          {/* Botao para abrir o modal de vizualização */}
                          <button
                            id="btnModal"
                            style={{ height: '40px', width: '40px', backgroundColor: '#51AFF3', borderRadius: 20, borderColor: '#51AFF3' }}
                            onClick={() => handleOpenModal(item)}
                          >
                            <FontAwesomeIcon icon={faEye} size="1x" color="#2759A4" />
                          </button>
                        </td>
                        <td>{item.id}</td>
                        <td>{item.name + ' ' + item.lastname}</td>
                      </tr>
                    ))}
                </tbody>
              </Table>
            </Container>
          </div>
        </div>
      </div>


                      {/* Modal */}
      {showModal && (
          <Modal className='modal' show={showModal} onHide={() => setShowModal(false)} centered>
            <Modal.Header>
              <Modal.Title style={{display: 'flex', justifyContent: 'center', fontSize: 'larger', marginBottom: '20px', marginTop: '20px'}}>
                <h4>
                  Detalhes do Atleta
                  </h4>
                  </Modal.Title>
            </Modal.Header>
            <Modal.Body style={{display: 'flex', justifyContent: 'center', fontSize: 'larger', marginBottom: '40px'}}>
              {selectedItem && (
                <div>
                  <p style={{display: 'flex', justifyContent: 'center'}}>ID: {selectedItem.id}</p>
                  <p style={{display: 'flex', justifyContent: 'center'}}>Nome: {selectedItem.name} {selectedItem.lastname}</p>
                  <p style={{display: 'flex', justifyContent: 'center'}}>Idade: {selectedItem.age}</p>
                  <p style={{display: 'flex', justifyContent: 'center'}}>Esporte: {selectedItem.sport}</p>
                  <p style={{display: 'flex', justifyContent: 'center'}}>Escola: {selectedItem.school}</p>
                </div>
              )}
            </Modal.Body>
            <Modal.Footer style={{display: 'flex', justifyContent: 'center'}}>
              <button style={{backgroundColor: '#51AFF3', borderRadius: 7, borderColor: '#2759A4', width: '100px', height: '30px'}} className="btn btn-primary" onClick={() => setShowModal(false)}>
                Fechar
              </button>
            </Modal.Footer>
          </Modal>
      )}

    </div>
  );
}

export default Visualiza;
