import React, { useState } from 'react';
import { View, Text, TouchableOpacity, Image, ScrollView } from 'react-native';
import { Avatar, Card, Input } from 'react-native-elements';
import styles from '../styles/styleGlobal';
import stylesL from '../styles/styleLogin';
import logoPrefeitura from '../assets/prefeituraLogo.png';

export default function Login({ navigation }) {
  const [usuario, setUsuario] = useState('');
  const [senha, setSenha] = useState('');
  const ExisteEsseUsuario = () => {
    if (usuario === 'adm' && senha === '123') {
      navigation.navigate('Formulario');
    } else {
      alert('Usu치rio ou senha inv치lido');
    }
  };

  return (
    <ScrollView style={styles.container}>
      <Card containerStyle={styles.card}>
        <View style={styles.jogaDladinho}>
          <Image source={logoPrefeitura} style={styles.logoPrefeitura} />
        </View>

        <Card.Divider />

        <Input
          type="Text"
          placeholder="Usuario"
          style={stylesL.input}
          value={usuario}
          onChangeText={setUsuario}
        />

        <Input
          type="Text"
          placeholder="Senha"
          style={stylesL.input}
          value={senha}
          onChangeText={setSenha}
        />

        <TouchableOpacity style={stylesL.botao} onPress={ExisteEsseUsuario}>
          <Text style={stylesL.textoDoBotao}>Login</Text>
        </TouchableOpacity>
      </Card>
    </ScrollView>
  );
}
