import React, { useState } from 'react';
import { View, Text, TouchableOpacity, Image, StyleSheet } from 'react-native';
import { Card, Input } from 'react-native-elements';
import logoPrefeitura from '../assets/LogoPrefeitura.png';

export default function Login({ navigation }) {
  const [usuario, setUsuario] = useState('');
  const [senha, setSenha] = useState('');

  const ExisteEsseUsuario = () => {
    if (usuario === 'adm' && senha === '123') {
      navigation.navigate('HomeTabs'); // Navegar para a tela do menu após o login
    } else {
      alert('Usuário ou senha inválido');
    }
  };

  return (
    <View style={styles.container}>
      <Image source={logoPrefeitura} style={styles.logoPrefeitura} />

      <Card>
        <Input
          placeholder="Usuário"
          value={usuario}
          onChangeText={setUsuario}
        />

        <Input
          placeholder="Senha"
          secureTextEntry
          value={senha}
          onChangeText={setSenha}
        />

        <TouchableOpacity style={styles.botao} onPress={ExisteEsseUsuario}>
          <Text style={styles.textoDoBotao}>Login</Text>
        </TouchableOpacity>
      </Card>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  logoPrefeitura: {
    width: 200,
    height: 200,
    marginBottom: 30,
  },
  botao: {
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
    height: 58,
    borderRadius: 5,
    backgroundColor: '#0000CD',

    marginTop: 20,
    marginBottom: 20,
  },

  textoDoBotao: {
    color: 'white',
    fontSize: 20,
    fontWeight: 'bold',
    textAlign: 'center',
  },

  input: {
    width: '100%',
    height: 50,
    paddingHorizontal: 10,
    fontSize: 20,
  }
});
