import React, { useState } from 'react';
import { View, Text, TouchableOpacity, Image, StyleSheet, ScrollView } from 'react-native';
import { Card, Input } from 'react-native-elements';
const avatar = require('../assets/logoEmblema.png');

export default function Login({ navigation }) {
  const [usuario, setUsuario] = useState('');
  const [senha, setSenha] = useState('');

  const ExisteEsseUsuario = () => {
    if (usuario === 'adm' && senha === '123') {
      navigation.navigate('Rotas'); // Navegar para a tela do menu após o login
    } else {
      alert('Usuário ou senha inválido');
    }
  };

  return (
    <ScrollView >
      <View style={stylesG.container}>
        <Image source={avatar} style={stylesG.logoPrefeitura} />
      </View>
      <card style={stylesG.card}>
      <Text style={stylesG.text}>Email</Text>
      <Input
        type="Text"
        placeholder="Usuario"
        style={stylesG.textInput}
        value={usuario}
        onChangeText={setUsuario}
      />
      <Text style={stylesG.text}>Senha</Text>
      <Input
        type="Text"
        placeholder="Senha"
        style={stylesG.textInput}
        value={senha}
        onChangeText={setSenha}
      />

      <TouchableOpacity style={stylesG.botao} onPress={ExisteEsseUsuario}>
        <Text style={stylesG.textoDoBotao}>Acessar</Text>
      </TouchableOpacity>
      </card>
    </ScrollView>
  );
}

const stylesG = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'flex-start',
    alignItems: 'center',
    backgroundColor: '#fff',
    marginTop: 30,

  },
card: {
  backgroundColor: '#fff',
},
  text: {
    fontWeight: 'bold',
    padding: 5,
    

  },

  jogaDladinho: {
    marginTop: 20,
    marginBottom: 20,
    flexDirection: 'row',
    justifyContent: 'center',
  },
  logoPrefeitura: {
    justifyContent: 'center',
    alignItems: 'center',
    width: 100,
    height: 100,
    marginBottom: 30,
  },
  botao: {
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
    height: 58,
    borderRadius: 5,
    backgroundColor: 'blue',
    marginTop: 20,
    marginBottom: 20,
  },

  textoDoBotao: {
    color: 'white',
    fontSize: 20,
    fontWeight: 'bold',
    textAlign: 'center',
  },

  Input: {
    height: 40,
    width: 330,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    marginBottom: 10,
    marginTop: 1,
    padding: 10,
  },
});
